mapper.select:
SELECT T.TEMPLATE_ID AS templateId,T.TITLE AS title,T.FLOW_ID AS flowId,F.FLOW_NAME AS flowName,T.CREATE_TIME AS createTime,
T.CREATE_USER AS createUser,(SELECT U.USER_NAME FROM USER_INFO U WHERE U.ORG_ID=#{orgId} AND U.ACCOUNT_ID=T.CREATE_USER) AS createUserName
FROM
BPM_BI_TEMPLATE T LEFT JOIN BPM_FLOW F ON T.ORG_ID=F.ORG_ID AND T.FLOW_ID=F.FLOW_ID
WHERE T.ORG_ID=#{orgId}

	AND T.FLOW_ID=#{flowId}


	AND T.TITLE LIKE #{search}
